import 'dart:convert';

import 'package:flutter/material.dart';

/// A material design widget that creates a [Card] widget that contains content
/// and actions about a single subject.
class DiveTopicCard extends StatelessWidget {
  final Widget? richMedia;
  final String? headerText;
  final String? subheadText;
  final String? supportingText;
  final List<String>? buttonTitles;
  final double width;
  const DiveTopicCard(
      {Key? key,
      this.richMedia,
      this.headerText,
      this.subheadText,
      this.supportingText,
      this.buttonTitles,
      this.width = 344})
      : super(key: key);

  @override
  Widget build(BuildContext context) {
    ButtonStyle bStyle = ButtonStyle(
      foregroundColor:
          MaterialStateProperty.all<Color>(const Color(0xFF6200EE)),
    );
    final buttons = buttonTitles == null
        ? null
        : buttonTitles!.map((title) => TextButton(
            style: bStyle,
            onPressed: () => null,
            child: Text(title.toUpperCase())));
    final card = Card(
      clipBehavior: Clip.antiAlias,
      child: Column(
        children: [
          if (richMedia != null) richMedia!,
          ListTile(
            title: headerText == null || headerText!.isEmpty
                ? null
                : Text(headerText!,
                    style: TextStyle(
                      color: Colors.black,
                      fontWeight: FontWeight.w500,
                      fontSize: 18.0,
                    )),
            subtitle: subheadText == null || subheadText!.isEmpty
                ? null
                : Text(
                    subheadText!,
                    style: TextStyle(
                      color: Colors.black.withOpacity(0.6),
                    ),
                  ),
          ),
          if (supportingText != null && supportingText!.isNotEmpty)
            Padding(
              padding: const EdgeInsets.all(16.0),
              child: Text(
                supportingText!,
                style: TextStyle(color: Colors.black.withOpacity(0.6)),
              ),
            ),
          if (buttons != null && buttons.isNotEmpty)
            ButtonBar(
                alignment: MainAxisAlignment.start, children: buttons.toList()),
        ],
      ),
    );
    return SizedBox(width: width, child: card);
  }

  factory DiveTopicCard.sample({int sampleNumber = 1}) {
    Widget? richMedia;
    dynamic buttonTitles = null;
    String headerText = 'Card title';
    String subheadText = 'Secondary text';
    String supportingText =
        'Greyhound divisively hello coldly wonderfully marginally far upon excluding.';
    switch (sampleNumber) {
      case 1:
        richMedia = Image.memory(base64Decode(demoImageUrl));
        // richMedia = Image.network(
        //     'https://bakingmischief.com/wp-content/uploads/2019/09/crispy-waffles-image-square.jpg');
        buttonTitles = ['Action 1', 'Action 2'];
        break;
      case 2:
        richMedia = Text('Centered Text');
        break;
      case 3:
        richMedia = Container(
          width: 180,
          height: 220,
          decoration: BoxDecoration(
              border: Border.all(color: Colors.purpleAccent.shade100)),
          child: Center(child: Text('Centered Text')),
        );
        break;
    }
    return DiveTopicCard(
        richMedia: richMedia,
        headerText: headerText,
        subheadText: subheadText,
        supportingText: supportingText,
        buttonTitles: buttonTitles);
  }

  static const demoImageUrl =
      "";
}
